import React from "react";
import { Link, useLocation } from "react-router-dom";
import { Box, Button } from "@mui/material";
import { styled, useTheme } from "@mui/material/styles";
import useMediaQuery from "@mui/material/useMediaQuery";

// Define the navigation items
const navItems = [
  {
    id: "home",
    label: "Home",
    path: "/",
    image: "/images/Bottle_Green.png",
    altImage: "/images/Bottle_Green_Alt.png",
  },
  {
    id: "treatments",
    label: "Treatments",
    path: "/treatments",
    image: "/images/Treatments.png",
    altImage: "/images/Treatments.png",
  },
  {
    id: "fees",
    label: "Fees",
    path: "/fees",
    image: "/images/Fees.png",
    altImage: "/images/Fees.png",
  },
  {
    id: "disclosures",
    label: "Disclosures",
    path: "/disclosures",
    image: "/images/Disclosure.png",
    altImage: "/images/Disclosure.png",
  },
  {
    id: "b77",
    label: "SB-77",
    path: "/sb77",
    image: "/images/B77.png",
    altImage: "/images/B77.png",
  },
  {
    id: "links",
    label: "Links",
    path: "/links",
    image: "/images/LinksAndArticles.png",
    altImage: "/images/LinksAndArticles.png",
  },
];

const NavButton = styled(Button)<{ $isMobile: boolean }>(({ theme, $isMobile }) => ({
  width: $isMobile ? '100%' : '100%', // Fixed width for mobile
  height: $isMobile ? '100px' : '220px',
  padding: 0,
  marginBottom: $isMobile ? 0 : theme.spacing(2),
  marginRight: $isMobile ? theme.spacing(2) : 0, // Add right margin for mobile
  flexShrink: 0, // Prevent shrinking in mobile view
  '&:hover': {
    backgroundColor: 'transparent',
  },
}));

const Navigation: React.FC = () => {
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('sm'));
  const location = useLocation();

  return (
    <Box sx={{ 
      width: '100%',
      // padding: theme.spacing(2),
      display: 'flex',
      flexDirection: isMobile ? 'row' : 'column',
      overflowX: isMobile ? 'auto' : 'visible', // Enable horizontal scroll on mobile
      overflowY: 'hidden', // Prevent vertical scroll
      WebkitOverflowScrolling: 'touch', // Smooth scrolling on iOS
      '&::-webkit-scrollbar': {
        display: 'none', // Hide scrollbar for WebKit browsers
      },
      scrollbarWidth: 'none', // Hide scrollbar for Firefox
      msOverflowStyle: 'none', // Hide scrollbar for IE and Edge
    }}>
      {navItems.map((item) => (
        <Link 
          key={item.id} 
          to={item.path} 
          style={{ 
            textDecoration: 'none', 
            display: 'block', 
            width: isMobile ? 'auto' : '100%',
            flexShrink: 0, // Prevent shrinking in mobile view
          }}
        >
          <NavButton
            $isMobile={isMobile}
          >
            <img
              src={location.pathname === item.path ? item.altImage : item.image}
              alt={item.label}
              style={{ 
                maxWidth: '100%', 
                height: '100%', 
                maxHeight: isMobile ? '100%' : '100%'
              }}
            />
          </NavButton>
        </Link>
      ))}
    </Box>
  );
};

export default Navigation;
